class Solution(object):
    def isPalindrome(self, s):
        """
        :type s: str
        :rtype: bool
        """
        s = ''.join(i for i in s if i.isalnum())
        s = s.lower()
        for j in xrange(len(s)):
            if s[j] != s[len(s) - j -1] and s[j] != s[len(s) - j-1].lower() :  #could be aA, 0P, or very long string
                return False
        return True


